<template>
  <ModalBase :modalName="modalName">
    <DialogTitle class="flex justify-between font-display">
      <p class="md:responsive-h2 text-3xl font-bold">
        {{ $t("components.modal_qr_code.header") }}
      </p>
    </DialogTitle>
    <div
      class="flex flex-col items-center space-y-6 pb-6 md:grid md:grid-cols-2 md:grid-rows-1 lg:mr-14 lg:grid-cols-3 lg:grid-rows-1 lg:space-x-6 lg:space-y-0 lg:pr-8"
    >
      <div class="col-span-2 items-center space-y-4 text-left font-medium">
        <p v-if="organization">
          {{
            $t("components.modal_qr_code.section_1_paragraph_1_organization")
          }}&nbsp;
          {{ $t("components.modal_qr_code.section_1_paragraph_1_2") }}
        </p>
        <p v-else-if="group">
          {{ $t("components.modal_qr_code.section_1_paragraph_1_group") }}&nbsp;
          {{ $t("components.modal_qr_code.section_1_paragraph_1_2") }}
        </p>
        <p v-else-if="event">
          {{ $t("components.modal_qr_code.section_1_paragraph_1_event") }}&nbsp;
          {{ $t("components.modal_qr_code.section_1_paragraph_1_2") }}
        </p>
        <p v-else-if="resource">
          {{
            $t("components.modal_qr_code.section_1_paragraph_1_resource")
          }}&nbsp;
          {{ $t("components.modal_qr_code.section_1_paragraph_1_2") }}
        </p>
        <p v-else-if="user">
          {{ $t("components.modal_qr_code.section_1_paragraph_1_user") }}&nbsp;
          {{ $t("components.modal_qr_code.section_1_paragraph_1_2") }}
        </p>
        <p>
          {{ $t("components.modal_qr_code.subheader_2") }}
        </p>
        <ul class="list-disc pl-6 md:pl-8">
          <li>
            {{ $t("components.modal_qr_code.section_2_list_1_item_1") }}
          </li>
          <li>
            {{ $t("components.modal_qr_code.section_2_list_1_item_2") }}
          </li>
          <li>
            {{ $t("components.modal_qr_code.section_2_list_1_item_3") }}
          </li>
        </ul>
        <!-- <p>
            {{ $t("components.modal_qr_code.section-3-paragraph-1") }}
          </p> -->
        <BtnActionDropdown
          v-if="aboveMediumBP"
          @main-btn-clicked="handleMainBtnClicked"
          class="block w-fit"
          :cta="true"
          :label="$t('components.modal_qr_code.download_qr_code')"
          fontSize="lg"
          iconSize="1.25em"
          :dropdownIcon="IconMap.CHEVRON_DOWN"
          :dropdownOptions="availableFormats"
          :dropdownOptionsCallback="downloadQRCode"
          ariaLabel="components.modal_qr_code.download_qr_code_aria_label"
          :ariaLabelDropdown="
            $t('components.modal_qr_code.qr_code_options_aria_label')
          "
        />
      </div>
      <div class="flex px-4 md:pb-2 md:pl-8">
        <button
          @click="onImageClick"
          @mouseenter="showTooltip = true"
          @focus="showTooltip = true"
          @mouseleave="showTooltip = false"
          @blur="showTooltip = false"
          @touchstart="showTooltip = true"
          @touchend="showTooltip = false"
          @touchcancel="showTooltip = false"
          @pointerdown="showTooltip = true"
          @pointerup="showTooltip = false"
          class="focus-brand flex cursor-pointer"
          :ariaLabel="$t('components.modal_qr_code.aria_label')"
        >
          <ModalQRCodeImage
            ref="qrcode"
            class="elem-shadow-md select-none rounded-3xl"
            :codeUrl="BASE_FRONTEND_URL + linkUrl"
          />
          <TooltipBase
            v-show="showTooltip"
            :text="$t('components.modal_qr_code.tooltip')"
          />
        </button>
      </div>
      <BtnActionDropdown
        v-if="!aboveMediumBP"
        @main-btn-clicked="handleMainBtnClicked"
        class="w-fit"
        :cta="true"
        :label="$t('components.modal_qr_code.download_qr_code')"
        fontSize="lg"
        iconSize="1.25em"
        :dropdownIcon="IconMap.CHEVRON_DOWN"
        :dropdownOptions="availableFormats"
        :dropdownOptionsCallback="downloadQRCode"
        :ariaLabel="$t('components.modal_qr_code.download_qr_code-aria-label')"
        :ariaLabelDropdown="
          $t('components.btn-action-dropdown.qr_code_options_aria_label')
        "
      />
    </div>
  </ModalBase>
</template>

<script setup lang="ts">
import { DialogTitle } from "@headlessui/vue";
import { useLinkURL } from "~/composables/useLinkURL";
import type { User } from "~/types/auth/user";
import type { Resource } from "~/types/content/resource";
import type { Group } from "~/types/entities/group";
import type { Organization } from "~/types/entities/organization";
import type { Event } from "~/types/events/event";
import { IconMap } from "~/types/icon-map";

const props = defineProps<{
  organization?: Organization;
  group?: Group;
  event?: Event;
  resource?: Resource;
  user?: User;
}>();

const aboveMediumBP = useBreakpoint("md");

const { linkUrl } = useLinkURL(props);
const modalName = "ModalsQRCode";

const showTooltip = ref(false);

const qrcode = ref();

const qrPixelGraphicsSize = computed(() => {
  if (qrcode?.value) {
    const size = qrcode!.value!.getSize();
    const width = 1500;
    const height: number = Math.round(width * (size.height / size.width));
    return { width: width, height: height };
  } else {
    return undefined;
  }
});

const availableFormats: Ref<string[]> = computed(() => {
  const size = qrPixelGraphicsSize.value;
  const qrCodeIsReady = size != undefined;
  return [
    "PNG" +
      (qrCodeIsReady
        ? ` (${size.width
            .toString()
            .replace(/\B(?=(\d{3})+(?!\d))/g, ",")} x ${size.height
            .toString()
            .replace(/\B(?=(\d{3})+(?!\d))/g, ",")} px)`
        : ""),
    "JPEG" +
      (qrCodeIsReady
        ? ` (${size.width
            .toString()
            .replace(/\B(?=(\d{3})+(?!\d))/g, ",")} x ${size.height
            .toString()
            .replace(/\B(?=(\d{3})+(?!\d))/g, ",")} px)`
        : ""),
    "SVG",
  ];
});

function drawInlineSVG(
  svgElement: Element,
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  ctx: any,
  width: number,
  height: number,
  callback: () => void
) {
  const svgUrl = new XMLSerializer().serializeToString(svgElement);
  const img = new Image();
  img.onload = function () {
    ctx.drawImage(this, 0, 0, width, height);
    callback();
  };
  img.src = "data:image/svg+xml; charset=utf8, " + encodeURIComponent(svgUrl);
}

const handleMainBtnClicked = () => {
  downloadQRCode("PNG");
};

let entityName = "";
if (props.organization) {
  entityName = props.organization.name;
} else if (props.group) {
  entityName = props.group.name;
} else if (props.event) {
  entityName = props.event.name;
} else if (props.resource) {
  entityName = props.resource.name;
} else if (props.user) {
  entityName = props.user.name;
}

const qrCodeFileName: string =
  "qr_code_" + entityName.toLowerCase().replaceAll(" ", "_");

function addFontStyling(svgElement: Element) {
  const def = document.createElement("def");
  def.innerHTML = `<style>
  /* latin-ext */
@font-face {
  font-family: 'Red Hat Display';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(data:font/woff2;base64,) format('woff2');
  unicode-range: U+0100-02AF, U+0304, U+0308, U+0329, U+1E00-1E9F, U+1EF2-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Red Hat Display';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(data:font/woff2;base64,) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0304, U+0308, U+0329, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
        </style>`;
  svgElement.appendChild(def);
}

function getPNGDataUrl(svgElement: Element): Promise<string> {
  addFontStyling(svgElement);
  const canvas = document.createElement("canvas");
  const size = qrPixelGraphicsSize.value;
  canvas.width = size!.width;
  canvas.height = size!.height;
  const ctx = canvas.getContext("2d")!;
  return new Promise((resolve) => {
    drawInlineSVG(svgElement, ctx, canvas.width, canvas.height, function () {
      resolve(canvas.toDataURL("image/png"));
    });
  });
}

function onImageClick() {
  const svgData = document.querySelector("#result-qr");
  if (svgData !== null) {
    getPNGDataUrl(svgData).then((url) => {
      const win = window.open()!;
      // win.document.write(`<iframe width="100%" height="100%" src="${url}"></iframe>`);
      win.document.write(`<style>
* {
  margin: 0;
  padding: 0;
}
div {
  display: grid;
  height: 100%;
}
img {
  max-width: 100%;
  max-height: 100vh;
  margin: auto;
}
      </style>`);
      win.document.write(`<div><img src="${url}"></img></div>`);
      win.document.write(`<script>
        window.addEventListener("keydown", function (event) {
          if (event.key === "s" && (event.ctrlKey || event.metaKey)) {
            const downloadLink = document.createElement("a");
            downloadLink.href = document.querySelector("img").src;
            downloadLink.download = "${qrCodeFileName}.png";
            document.body.appendChild(downloadLink);
            downloadLink.click();
            document.body.removeChild(downloadLink);
          }
        }, false);
      <${"/"}script>`);
    });
  }
}

function downloadQRCode(format: string) {
  const svgData = document.querySelector("#result-qr")!;
  const def = document.createElement("def");
  def.innerHTML = `<style>
  /* latin-ext */
@font-face {
  font-family: 'Red Hat Display';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(data:font/woff2;base64,) format('woff2');
  unicode-range: U+0100-02AF, U+0304, U+0308, U+0329, U+1E00-1E9F, U+1EF2-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Red Hat Display';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(data:font/woff2;base64,) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0304, U+0308, U+0329, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
        </style>`;
  svgData.appendChild(def);
  if (format == "SVG") {
    const svgBlob = new Blob([svgData.outerHTML], {
      type: "image/svg+xml;charset=utf-8",
    });
    const svgUrl = URL.createObjectURL(svgBlob);
    const downloadLink = document.createElement("a");
    downloadLink.href = svgUrl;
    downloadLink.download = `${qrCodeFileName}.svg`;
    document.body.appendChild(downloadLink);
    downloadLink.click();
    document.body.removeChild(downloadLink);
  } else if (format.startsWith("PNG") || format.startsWith("JPEG")) {
    const canvas: HTMLCanvasElement = document.createElement("canvas");
    const size = qrPixelGraphicsSize.value;
    canvas.width = size!.width;
    canvas.height = size!.height;
    const ctx = canvas.getContext("2d")!;
    const isJPEG = format.startsWith("JPEG");
    if (isJPEG) {
      // Avoid black background in jpeg files.
      ctx.fillStyle = "white";
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }
    const fileExtension = isJPEG ? "jpeg" : "png";
    drawInlineSVG(svgData, ctx, canvas.width, canvas.height, function () {
      const downloadLink = document.createElement("a");
      downloadLink.href = canvas.toDataURL(`image/${fileExtension}`, 1.0);
      downloadLink.download = `${qrCodeFileName}.${fileExtension}`;
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
    });
  }
}
</script>
